    JOIN auth.user_profile_s ups ON uh.user_hk = ups.user_hk
    WHERE ups.email = v_user_id
    AND uh.tenant_hk = v_tenant_hk
    AND ups.load_end_date IS NULL;
    
    IF v_tenant_hk IS NULL OR v_user_hk IS NULL THEN
