        jsonb_build_object(
            'tenant_id', v_tenant_id,
            'user_id', v_user_id,
            'session_id', v_session_id,
            'context_type', v_context_type,
            'limit', v_limit,
            'offset', v_offset,
            'records_returned', COALESCE(jsonb_array_length(v_history_data), 0),
