        v_load_date,
        util.hash_binary(p_email || 'USER_AUTH' || CURRENT_TIMESTAMP::text),
        p_email,
        v_password_hash::BYTEA,
        v_salt::BYTEA,
        NULL,
        CURRENT_TIMESTAMP,
        0,
        FALSE,
        FALSE,
