    SELECT * INTO v_current_token
    FROM auth.api_token_s
    WHERE token_hash = p_token_hash
    AND load_end_date IS NULL
    ORDER BY load_date DESC
    LIMIT 1;

    IF NOT FOUND THEN
        RETURN FALSE;
    END IF;

    v_api_token_hk := v_current_token.api_token_hk;

