        v_backup_hk, 
        util.current_load_date() + INTERVAL '1 microsecond', -- Ensure different timestamp
        NULL,
        util.hash_binary(v_backup_bk || v_backup_status),
        'FULL', v_backup_scope, 'PG_BASEBACKUP',
        v_start_time, v_end_time, v_duration, v_backup_status,
