        'SITE_TRACKING'::VARCHAR(100),
        'complete_pipeline'::VARCHAR(255),
        SESSION_USER::VARCHAR(100),
        v_processing_summary
    );
    
    RETURN QUERY SELECT 
        v_total_processed,
        v_raw_results.processed_count,
        v_business_results.processed_count,
        v_total_success,
        v_total_errors,
        v_processing_summary;
END;
